object HigherOrderFun {  def sum(f: Int => Int, a: Int, b: Int): Int = {    if (a > b) 0    else f(a) + sum(f, a+1, b)  };  def identity(x: Int): Int = x;  def cube(x: Int): Int = x * x * x;  def square(x: Int): Int = x * x * x;  def main(args: Array[String]): Unit = {    println(s"Sum of Number:, ${sum(identity, 1, 2)}");    println(s"Sum of Squares:, ${sum(square, 1, 2)}");    println(s"Sum of Cubes:, ${sum(cube, 1, 2)}");  }}